function sendMessage(e){const t=document.querySelector("iframe.giscus-frame");t&&t.contentWindow.postMessage({giscus:e},"https://giscus.app")}function toggleNightMode(){"light"==document.documentElement.getAttribute("data-theme")?(document.documentElement.setAttribute("data-theme","dark"),document.getElementById("mode-switcher").classList.add("active"),sendMessage({setConfig:{theme:"dark"}}),localStorage.setItem("theme","dark")):(document.documentElement.setAttribute("data-theme","light"),document.getElementById("mode-switcher").classList.remove("active"),sendMessage({setConfig:{theme:"light"}}),localStorage.setItem("theme",""))}document.querySelector("iframe.giscus-frame").addEventListener("load",()=>{sendMessage({setConfig:{theme:document.documentElement.getAttribute("data-theme")}})});